import React, { useState } from "react";

function App() {
  const [selectedFiles, setSelectedFiles] = useState([]);

  const handleFileInput = (e) => {
    setSelectedFiles(e.target.files);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!Array.isArray(selectedFiles) || selectedFiles.length === 0) {
        return;
  }

    // Initialize the upload for all files
    const uploadIds = await Promise.all(
      selectedFiles.map((file) => {
        const formData = new FormData();
        formData.append("file", file);
        formData.append("fileName", file.name);
        return fetch("http://0.0.0.0:8000/initiate-upload", {
          method: "POST",
          body: formData,
        })
          .then((response) => response.json())
          .then((data) => data.uploadId);
      })
    );
     console.log(formData)

    // Upload all chunks for each file
    await Promise.all(
      selectedFiles.map(async (file, index) => {
        const CHUNK_SIZE = 1024 * 1024; // 1MB
        const fileSize = file.size;
        const chunks = Math.ceil(fileSize / CHUNK_SIZE);

        const uploadUrl = `http://0.0.0.0:8000/upload?fileName=${encodeURIComponent(
          file.name
        )}&uploadId=${encodeURIComponent(uploadIds[index])}`;

        const chunkPromises = [];

        for (let chunkIndex = 0; chunkIndex < chunks; chunkIndex++) {
          const start = chunkIndex * CHUNK_SIZE;
          const end = Math.min(start + CHUNK_SIZE, fileSize);
          const chunk = file.slice(start, end);

          const formData = new FormData();
          formData.append("file", chunk);
          formData.append("chunkIndex", chunkIndex);
          formData.append("chunks", chunks);

          chunkPromises.push(
            fetch(uploadUrl, {
              method: "POST",
              body: formData,
            })
          );
        }

        await Promise.all(chunkPromises);
      })
    );

    console.log("Files uploaded successfully!");
  };

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <input type="file" multiple onChange={handleFileInput} />
        <button type="submit">Upload</button>
      </form>
    </div>
  );
}

export default App;
