import React, {useState, useRef, useEffect} from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [uploadId, setUploadId] = useState('');


  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  var ui = ''


  // const initiateUpload = async () => {
  //   const options = {
  //     headers: {
  //       'Content-Type': 'application/octet-stream',
  //       'x-amz-acl': 'private'
  //     }
  //   };
  //   const response = await axios.post(
  //     `http://localhost:8000/initiate-upload?filename=${file.name}`,
  //     null,
  //     options
  //   );
  //   ui = response.data.upload_id;
  // };


  const initiateUpload = async () => {
    const formData = new FormData();
    formData.append('filename', file.name);
    await axios.post(`http://localhost:8000/initiate-upload?filename=${file.name}`)
        .then((res) => {
          ui = res.data.upload_id
        })
  };


  const uploadChunk = async (chunkNumber, chunk) => {
    const data = new FormData()
    data.append('filename', file.name)
    data.append('chunk_number', chunkNumber)
    data.append('chunk', chunk)
    data.append('upload_id', ui)
    console.log(data)
  const options = {
    headers: {
      'Content-Type': 'application/octet-stream',
      'x-amz-acl': 'private',
      'x-amz-part-number': chunkNumber + 1,
      'x-amz-upload-id': uploadId,
      'Content-Length': chunk.byteLength
    }
  };
  // const blob = new Blob([chunk]);
  await axios.post(`http://localhost:8000/upload-chunk`, data, options
  ).then((res) => {
    console.log(res.data)
  });
};

  const completeUpload = async () => {
    await axios.post(`http://localhost:8000/complete-upload?filename=${file.name}&upload_id=${ui}`).then((res) => {
      console.log(res.data)
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const chunkSize = 100 * 1024 * 1024;
    const chunks = [];
    const reader = new FileReader();
    reader.onload = async (event) => {
      try {
        const arrayBuffer = event.target.result;
        const totalSize = arrayBuffer.byteLength;
        let start = 0;
        while (start < totalSize) {
          const end = Math.min(start + chunkSize, totalSize);
          const chunk = new Blob([arrayBuffer.slice(start, end)], { type: 'application/octet-stream' });
          chunks.push(chunk);
          start = end;
        }
        await initiateUpload();

        const promises = [];
        for (let i = 0; i < chunks.length; i++) {
          const chunkSizeMb = chunks[i].size;
          console.log(chunks[i].size);
          console.log(`Chunk ${i} size: ${chunkSizeMb} MB`);
          promises.push(uploadChunk(i, chunks[i]));
        }
        await Promise.all(promises);
        await completeUpload();
        } catch (error) {
    console.log(error);
  }
      };

    reader.readAsArrayBuffer(file);
  };


  return (
    <form onSubmit={handleSubmit}>
      <input type="file" onChange={handleFileChange} />
      <button type="submit">Upload</button>
    </form>
  )
}

export default App;
