import React, {useEffect, useState} from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);

  const handleFileChange = (event) => {
    const formData = new FormData()
    formData.append('filename', event.target.files[0].name)
    setFile(formData);
  };

  const initiateUpload = async () => {

    // const formData = new FormData();
    //
    // formData.append('filename', file.name);



    await axios.post('http://localhost:8000/initiate-upload', file).then((res) => console.log(res));
    // return response.data.upload_id;
  };

  const uploadChunk = async (chunkNumber, chunk, uploadId) => {
  const formData = new FormData();
  formData.append('filename', file.name);
  formData.append('chunk_number', chunkNumber);
  formData.append('chunk', chunk);
  formData.append('upload_id', uploadId);
  await axios.post('http://localhost:8000/upload-chunk', formData);
};

  const completeUpload = async () => {
    const formData = new FormData();
    formData.append('filename', file.name);
    await axios.post('http://localhost:8000/complete-upload', formData);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const chunkSize = 5 * 1024 * 1024; // 5 MB
    const chunks = [];
    const reader = new FileReader();
    reader.onload = async (event) => {
      const arrayBuffer = event.target.result;
      const totalSize = arrayBuffer.byteLength;
      let start = 0;
      while (start < totalSize) {
        const end = Math.min(start + chunkSize, totalSize);
        const chunk = arrayBuffer.slice(start, end);
        chunks.push(chunk);
        start = end;
      }
      await initiateUpload();
      const promises = [];
      for (let i = 0; i < chunks.length; i++) {
        promises.push(uploadChunk(i, chunks[i]));
      }
      await Promise.all(promises);
      await completeUpload();
    };
    reader.readAsArrayBuffer(file);
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="file" onChange={handleFileChange} />
      <button type="submit">Upload</button>
    </form>
  )
}

export default App;
