import React, {useState, useRef, useEffect} from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [uploadId, setUploadId] = useState('');


  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  var ui = ''


  // const initiateUpload = async () => {
  //   const options = {
  //     headers: {
  //       'Content-Type': 'application/octet-stream',
  //       'x-amz-acl': 'private'
  //     }
  //   };
  //   const response = await axios.post(
  //     `http://localhost:8000/initiate-upload?filename=${file.name}`,
  //     null,
  //     options
  //   );
  //   ui = response.data.upload_id;
  // };


  const initiateUpload = async () => {
    const formData = new FormData();
    formData.append('filename', file.name);
    await axios.post(`http://localhost:8000/initiate-upload?filename=${file.name}`)
        .then((res) => {
          ui = res.data.upload_id
        })
  };


  const uploadChunk = async (chunkNumber, chunk) => {

    const arrayBuffer = await chunk.arrayBuffer(); // convert blob to array buffer
    const byteArray = new Uint8Array(arrayBuffer); // convert array buffer to byte array
    // const data = new FormData();
    // const data = new FormData()
    console.log(chunk, 'asd')

    const data = {
      filename: file.name,
      chunk_number: chunkNumber,
      chunk: new Blob([byteArray], {type: 'application/octet-stream'}),
      upload_id: ui
    }
  //   data.append('filename', file.name);
  // data.append('chunk_number', chunkNumber);
  // data.append('chunk', new Blob([byteArray], {type: 'application/octet-stream'}));
  // data.append('upload_id', ui);




  await axios.post(`http://localhost:8000/upload-chunk`, data).then((res) => {
    console.log(res.data)
  });
};

  const completeUpload = async () => {
    await axios.post(`http://localhost:8000/complete-upload?filename=${file.name}&upload_id=${ui}`).then((res) => {
      console.log(res.data)
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const chunkSize = 100 * 1024 * 1024;
    const chunks = [];
    const reader = new FileReader();
    reader.onload = async (event) => {
      const arrayBuffer = event.target.result;
      const totalSize = arrayBuffer.byteLength;
      let start = 0;
      while (start < totalSize) {
        const end = Math.min(start + chunkSize, totalSize);
        const chunk = new Blob([arrayBuffer.slice(start, end)], { type: 'application/octet-stream' });
        chunks.push(chunk);
        start = end;
      }
      await initiateUpload();
      const promises = chunks.map((chunk, index) => uploadChunk(index, chunk));
      await Promise.all(promises);
      await completeUpload();
    };
    reader.readAsArrayBuffer(file);
  };


  return (
    <form onSubmit={handleSubmit}>
      <input type="file" onChange={handleFileChange} />
      <button type="submit">Upload</button>
    </form>
  )
}

export default App;
