import React, {useState, useRef, useEffect} from 'react';
import axios from 'axios';

function App() {


  const uploadChunk = async () => {
    for (let i = 0; i < files.length; i++) {
      const file = files[i];
      const chunkSize = 100 * 1024 * 1024; // 5 MB
      const fileSize = file.size;
      const chunks = Math.ceil(fileSize / chunkSize);
      let offset = 0;
      for (let j = 0; j < chunks; j++) {
        const chunk = file.slice(offset, offset + chunkSize);
        const chunkNumber = j;
        const uploadChunkFormData = new FormData();

        uploadChunkFormData.append('filename', filename);
        uploadChunkFormData.append('chunk_number', chunkNumber);
        uploadChunkFormData.append('chunk', new Blob([chunk]));
        uploadChunkFormData.append('upload_id', uploadId);


        // const boundary = formdata.getBoundary()
        const response = await fetch('http://localhost:8000/upload-chunk', {
          method: 'POST',
          body: uploadChunkFormData,
          headers: {
            'Content-Type': `multipart/form-data`
          }
        });
        const data = await response.json();
        console.log(data);
        offset += chunkSize;
      }
    }
  }


  async function uploadFile() {
    const fileInput = document.querySelector('input[type="file"]');
    const files = fileInput.files;
    const filename = files[0].name;

    const formdata = new FormData()
    formdata.append('filename', filename)

    let uploadId
    await fetch(`http://localhost:8000/initiate-upload/`,{
      method: 'POST',
      body: formdata
    }).then(res => {
      res.json()
    }).then(data => {
      uploadId = data.upload_id
    });



    await uploadChunk()

    const response2 = await fetch('http://localhost:8000/complete-upload', {
      method: 'POST',
      headers: {
        'Content-Type': 'multipart/form-data'
      },
      body: JSON.stringify({filename: filename, upload_id: uploadId})
    });
    const data2 = await response2.json();
    console.log(data2);
  }


  return (
      <>
        <form encType="multipart/form-data">
        <input type="file" name="file" multiple/>
          <button type="button" onClick={uploadFile}>Upload</button>
      </form>
      </>
  )
}

export default App;
