import React, {useState, useRef, useEffect} from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [uploadId, setUploadId] = useState('');


  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };


  const initiateUpload = async () => {
    const formData = new FormData();
    formData.append('filename', file.name);
    await axios.post(`http://localhost:8000/initiate-upload?filename=${file.name}`).then((res) =>
      setUploadId(res.data.upload_id)
    );
  };

  const uploadChunk = async (chunkNumber, chunk) => {
    await axios.post(`http://localhost:8000/upload-chunk?filename=${file.name}&chunk_number=${chunkNumber}&chunk=${chunk}&upload_id=${uploadId}`).then((res) => {
      console.log(res.data)
    });
  };

  const completeUpload = async () => {
    await axios.post(`http://localhost:8000/complete-upload?filename=${file.name}&upload_id=${uploadId}`).then((res) => {
      console.log(res.data)
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const chunkSize = 100 * 1024 * 1024; // 5 MB
    const chunks = [];
    const reader = new FileReader();
    reader.onload = async (event) => {
      const arrayBuffer = event.target.result;
      const totalSize = arrayBuffer.byteLength;
      let start = 0;
      while (start < totalSize) {
        const end = Math.min(start + chunkSize, totalSize);
        const chunk = arrayBuffer.slice(start, end);
        chunks.push(chunk);
        start = end;
      }
      await initiateUpload();

      const promises = [];
      for (let i = 0; i < chunks.length; i++) {
        promises.push(uploadChunk(i, chunks[i]));
      }
      await Promise.all(promises);
      await completeUpload();
    };

    reader.readAsArrayBuffer(file);
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="file" onChange={handleFileChange} />
      <button type="submit">Upload</button>
    </form>
  )
}

export default App;
